# Question #5
def gcd(x, y):
	if(y > x):
		temp = y
		y = x
		x = temp
	while(y > 0):
		temp = x
		x = y
		y = temp%y
	return x
def lcm(n):
	temp = 1
	for i in range(1, n):
		temp_gcd = gcd(i, temp)
		temp *= i/temp_gcd
	print(temp) 
